            //var v = $"public static byte[] KeyTyperIconBytes = [{string.Join(", ", bytes.Select(x => x.ToString()))}];";
            //Console.WriteLine(v);
            //return;

            Application.EnableVisualStyles();
            Application.SetCompatibleTextRenderingDefault(false);
            Application.Run(new Program().Show());
        }

        private DateTime _startedAt = DateTime.Now;
        private bool _started = false;

        // https://learn.microsoft.com/en-us/dotnet/api/system.windows.forms.sendkeys?view=windowsdesktop-10.0
        private readonly char[] ReplaceCharsForSendKeys = [
            '{', '}', '+', '^', '%', '~', '(', ')', '[', ']'
            ];

        public Form Show()
        {
            var form = new Form();
            form.Text = "KeyTyper";
            form.Size = new Size(500, 300);
            form.MinimumSize = new Size(300, 200);
            using (var ms = new MemoryStream(KeyTyperIconBytes))
            {
                form.Icon = Icon.FromHandle(new Bitmap(ms).GetHicon());
            }

            form.Padding = new Padding(10);

            var topPanel = new Panel();
            topPanel.Dock = DockStyle.Top;
            topPanel.Height = 25;

            var alwaysOnTopCheckbox = new CheckBox();
            alwaysOnTopCheckbox.Checked = form.TopMost;
            alwaysOnTopCheckbox.Text = "Always on top";
            alwaysOnTopCheckbox.Dock = DockStyle.Left;
            alwaysOnTopCheckbox.Click += (sender, args) => form.TopMost = alwaysOnTopCheckbox.Checked;
            topPanel.Controls.Add(alwaysOnTopCheckbox);

            var timeBufferMsInput = new NumericUpDown();
            timeBufferMsInput.Minimum = 500;
            timeBufferMsInput.Maximum = 30000;
            timeBufferMsInput.Value = 2000;
            timeBufferMsInput.Dock = DockStyle.Right;
            topPanel.Controls.Add(timeBufferMsInput);

            var displayNumber = new Label();
            displayNumber.Text = "-";
            displayNumber.Dock = DockStyle.Right;
            displayNumber.TextAlign = ContentAlignment.MiddleRight;
            topPanel.Controls.Add(displayNumber);

            form.Controls.Add(topPanel);

            var txtInput = new RichTextBox();
            txtInput.Dock = DockStyle.Fill;
            form.Controls.Add(txtInput);
            txtInput.BringToFront();

            var timer = new Timer();
            timer.Interval = 100;
            timer.Tick += (sender, args) =>
            {
                if (!_started)
                {
                    displayNumber.Text = "-";
                    return;
                }

                var now = DateTime.Now;
                var timeBufferMs = (double)timeBufferMsInput.Value;
                if ((now - _startedAt).TotalMilliseconds > timeBufferMs)
                {
                    _started = false;
                    var txt = txtInput.Text;
                    txtInput.Text = string.Empty;
                    txtInput.Update();
                    displayNumber.Text = "Typing";
                    displayNumber.Update();

                    var sb = new StringBuilder(txt.Length + 10);
                    foreach (var c in txt)
                    {
                        if (ReplaceCharsForSendKeys.Contains(c))
                            sb.Append("{" + c + "}");
                        else
                            sb.Append(c);
                    }
                    SendKeys.SendWait(sb.ToString());

                    displayNumber.Text = "-";
                    displayNumber.Update();
                }
                else
                {
                    displayNumber.Text = ((timeBufferMs - (now - _startedAt).TotalMilliseconds) / 1000).ToString("N2") + "ms";
                }

            };
            timer.Start();
            timer.Enabled = true;

            var submitButton = new Button();
            submitButton.Text = "Submit";
            submitButton.Dock = DockStyle.Bottom;
            submitButton.Height = 50;
            submitButton.Click += (sender, args) =>
            {
                _startedAt = DateTime.Now;
                _started = true;
            };

            submitButton.Enabled = false;
            txtInput.TextChanged += (sender, args) => submitButton.Enabled = !string.IsNullOrEmpty(txtInput.Text);

            form.Controls.Add(submitButton);

            return form;
        }
    }
